CREATE FUNCTION [dbo].[fClosingPeriod]
(
    @SiteID uniqueidentifier,
    @YearID int,
    @SiteCode nvarchar(10),
    @SiteName nvarchar(50),
    @AreaID int,
    @AreaCode nvarchar(10),
    @AreaName nvarchar(50),
    @RegionID int,
    @RegionCode nvarchar(10),
    @RegionName nvarchar(50),
    @TerritoryID int,
    @TerritoryCode nvarchar(10),
    @TerritoryName nvarchar(50),
    @CompanyID int,
    @CompanyCode nvarchar(10),
    @CompanyName nvarchar(50),
    @SiteDistributionTypeID int,
    @IsSiteDeleted bit
)
RETURNS TABLE 
AS
RETURN 
(
	SELECT
            [CP].[SiteID],
            [CP].[YearID],
            [CP].[Jan],
            [CP].[Feb],
            [CP].[Mar],
            [CP].[Apr],
            [CP].[May],
            [CP].[Jun],
            [CP].[Jul],
            [CP].[Aug],
            [CP].[Sep],
            [CP].[Oct],
            [CP].[Nov],
            [CP].[Dec],
            [fS].[Code] [SiteCode],
            [fS].[Name] [SiteName],
            [fS].[Site],
            [fS].[AreaID],
            [fS].[AreaCode],
            [fS].[AreaName],
            [fS].[Area],            
            [fS].[RegionID],
            [fS].[RegionCode],
            [fS].[RegionName],
            [fS].[Region],
            [fS].[TerritoryID],
            [fS].[TerritoryCode],
            [fS].[TerritoryName],
            [fS].[Territory],
            [fS].[CompanyID],
            [fS].[CompanyCode],
            [fS].[CompanyName],
            [fS].[Company],
            [fS].[DistributionTypeID] [SiteDistributionTypeID],
            [fS].[DistributionTypeName] [SiteDistributionTypeName],
            [CP].[CreatedDate],
            [CP].[CreatedByUserID],
            [CU].[Name] [CreatedByUserName],
            [CP].[ModifiedDate],
            [CP].[ModifiedByUserID],
            [MU].[Name] [ModifiedByUserName]
        FROM
			[dbo].[ClosingPeriod] [CP] INNER JOIN
            [dbo].[fSite]
            (
                @SiteID,
                @SiteCode,
                @SiteName,
                @AreaID,
                @AreaCode,
                @AreaName,
                @RegionID,
                @RegionCode,
                @RegionName,
                @TerritoryID,
                @TerritoryCode,
                @TerritoryName,
                @CompanyID,
                @CompanyCode,
                @CompanyName,
                @SiteDistributionTypeID,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                NULL,
                @IsSiteDeleted
            ) [fS] ON ([CP].[SiteID] = [fS].[ID]) LEFT OUTER JOIN
            [dbo].[User] [CU] ON ([CP].[CreatedByUserID] = [CU].[ID]) LEFT OUTER JOIN
            [dbo].[User] [MU] ON ([CP].[ModifiedByUserID] = [MU].[ID])
		WHERE
            ((@SiteID IS NULL) OR ([CP].[SiteID] = @SiteID)) AND
            ((@YearID IS NULL) OR ([CP].[YearID] = @YearID))
);
GO



