-- ===================================================================================
-- Author        : System
-- Created date  : 20 Mar 2016 23:32:11
-- Description   : Provide function to retrieve [dbo].[MerchandiseDisplayActivity] data.
--
-- NOTE: This Function is initially generated by system and can be modified following
--       the requirement.
-- ===================================================================================

CREATE FUNCTION [dbo].[fMerchandiseDisplayActivity]
(
    @ID uniqueidentifier,
    @ActivityDateFrom datetime,
    @ActivityDateTo datetime,
    @SalesmanID uniqueidentifier,
    @CallDateFrom datetime,
    @CallDateTo datetime,
    @CustomerID uniqueidentifier    
)
RETURNS TABLE 
AS
RETURN 
(
	SELECT
            [MDA].[ID],
            [MDA].[ActivityDate],
            [MDA].[SalesmanCallID],            
            [SC].[SalesmanID],
            [SC].[CallDate],
            [SC].[CustomerID],
            [MDA].[AttachmentFile],
            [MDA].[CreatedDate],
            [MDA].[CreatedByUserID],
            [CU].[Name] [CreatedByUserName]
        FROM
			[dbo].[MerchandiseDisplayActivity] [MDA] INNER JOIN
            [dbo].[SalesmanCall] [SC] ON ([MDA].[SalesmanCallID] = [SC].[ID]) LEFT OUTER JOIN
            [dbo].[User] [CU] ON ([MDA].[CreatedByUserID] = [CU].[ID])
		WHERE
            ((@ActivityDateFrom IS NULL) OR ([dbo].[ValidateMaxDate]([MDA].[ActivityDate]) >= [dbo].[ValidateMinDate](@ActivityDateFrom))) AND
            ((@ActivityDateTo IS NULL) OR ([dbo].[ValidateMinDate]([MDA].[ActivityDate]) <= [dbo].[ValidateMaxDate](@ActivityDateTo))) AND
            ((@SalesmanID IS NULL) OR (@SalesmanID = [SC].[ID])) AND
            ((@CallDateFrom IS NULL) OR ([dbo].[ConvertToFirstTimeOfDay]([SC].[CallDate]) >= [dbo].[ConvertToFirstTimeOfDay](@CallDateFrom))) AND
            ((@CallDateTo IS NULL) OR ([dbo].[ConvertToLastTimeOfDay]([SC].[CallDate]) <= [dbo].[ConvertToLastTimeOfDay](@CallDateTo))) AND
            ((@CustomerID IS NULL) OR ([SC].[CustomerID] = @CustomerID))            
);
GO



