                var qtyOrder = DXUtility.getValue(e.newData, 'QtyOrder');
                if (qtyOrder == undefined)
                    qtyOrder = DXUtility.getValue(e.oldData, 'QtyOrder');
                
                if (qtyOrder <= 0) {
                    e.errorText = 'Order Qty must be greater than 0.';
                    e.isValid = false;
                }

