using Dismoyo.Data;
using Dismoyo.Web.Security;
using System;
using System.Linq;
using System.Web;

namespace Dismoyo.Ciptoning.Data
{

    public partial class DiscountGroupDataProvider : DefaultTableDataProvider<DiscountGroup>, IDiscountGroupDataProvider
    {

        #region Methods

        public DiscountGroup GetDataByCode(string code)
        {
            var discountGroup = GetData().SingleOrDefault(p => p.Code.Equals(code, StringComparison.OrdinalIgnoreCase));

            return discountGroup;
        }


        protected override void OnBeginInsertData(BeginOperationDataEventArgs<DiscountGroup> e)
        {
            e.Data.CreatedDate = DefaultDataContext.GetDBServerUtcDateTime();
            if (HttpContext.Current.User.Identity.IsAuthenticated)
                e.Data.CreatedByUserID = ((BasicGenericIdentity<IvUser>)HttpContext.Current.User.Identity).UserData.ID;

            base.OnBeginInsertData(e);
        }

        protected override void OnBeginUpdateData(BeginOperationDataEventArgs<DiscountGroup> e)
        {
            e.Data.ModifiedDate = DefaultDataContext.GetDBServerUtcDateTime();
            if (HttpContext.Current.User.Identity.IsAuthenticated)
                e.Data.ModifiedByUserID = ((BasicGenericIdentity<IvUser>)HttpContext.Current.User.Identity).UserData.ID;

            base.OnBeginUpdateData(e);
        }

        public override void DeleteData(DiscountGroup obj, bool useTransaction)
        {
            var discountGroupProductDataProvider =
                DataConfiguration.GetDefaultDataProvider<IDiscountGroupProductDataProvider>();

            var deleteChilds = discountGroupProductDataProvider.GetDataByDiscountGroupID(obj.ID);
            foreach (var child in deleteChilds)
                discountGroupProductDataProvider.DeleteData(child, useTransaction);

            base.DeleteData(obj);
        }

        #endregion

    }

}
