using System;
using System.Collections.Generic;
using System.Linq;
using Dismoyo.Data;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class SalesOrderFOCSummaryDataProvider : DefaultTableDataProvider<SalesOrderFOCSummary>, ISalesOrderFOCSummaryDataProvider
    {

        #region Methods

        public IList<SalesOrderFOCSummary> GetDataByDocumentID(Guid documentID)
        {
            var salesOrderFOCSummaries = GetData().Where(p => p.DocumentID.Equals(documentID)).ToList();

            return salesOrderFOCSummaries;
        }


        public override void DeleteData(SalesOrderFOCSummary obj, bool useTransaction)
        {
            var salesOrderFOCDetailsDataProvider =
                DataConfiguration.GetDefaultDataProvider<ISalesOrderFOCDetailsDataProvider>();

            var deleteChilds = salesOrderFOCDetailsDataProvider.GetDataByDocumentIDAndProductID(
                obj.DocumentID, obj.ProductID);
            foreach (var child in deleteChilds)
                salesOrderFOCDetailsDataProvider.DeleteData(child, useTransaction);

            base.DeleteData(obj, useTransaction);
        }

        #endregion

    }

}
