using System;
using System.Collections.Generic;
using System.Linq;
using Dismoyo.Data;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class SalesOrderReturnSummaryDataProvider : DefaultTableDataProvider<SalesOrderReturnSummary>, ISalesOrderReturnSummaryDataProvider
    {

        #region Methods

        public IList<SalesOrderReturnSummary> GetDataByDocumentID(Guid documentID)
        {
            var salesOrderReturnSummaries = GetData().Where(p => p.DocumentID.Equals(documentID)).ToList();

            return salesOrderReturnSummaries;
        }


        public override void DeleteData(SalesOrderReturnSummary obj, bool useTransaction)
        {
            var salesOrderReturnDetailsDataProvider =
                DataConfiguration.GetDefaultDataProvider<ISalesOrderReturnDetailsDataProvider>();

            var deleteChilds = salesOrderReturnDetailsDataProvider.GetDataByDocumentIDAndProductID(
                obj.DocumentID, obj.ProductID);
            foreach (var child in deleteChilds)
                salesOrderReturnDetailsDataProvider.DeleteData(child, useTransaction);

            base.DeleteData(obj, useTransaction);
        }

        #endregion

    }

}
