using System;
using System.Collections.Generic;
using System.Linq;
using Dismoyo.Data;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class SalesOrderSampleSummaryDataProvider : DefaultTableDataProvider<SalesOrderSampleSummary>, ISalesOrderSampleSummaryDataProvider
    {

        #region Methods

        public IList<SalesOrderSampleSummary> GetDataByDocumentID(Guid documentID)
        {
            var salesOrderSampleSummaries = GetData().Where(p => p.DocumentID.Equals(documentID)).ToList();

            return salesOrderSampleSummaries;
        }


        public override void DeleteData(SalesOrderSampleSummary obj, bool useTransaction)
        {
            var salesOrderSampleDetailsDataProvider =
                DataConfiguration.GetDefaultDataProvider<ISalesOrderSampleDetailsDataProvider>();

            var deleteChilds = salesOrderSampleDetailsDataProvider.GetDataByDocumentIDAndProductID(
                obj.DocumentID, obj.ProductID);
            foreach (var child in deleteChilds)
                salesOrderSampleDetailsDataProvider.DeleteData(child, useTransaction);

            base.DeleteData(obj, useTransaction);
        }

        #endregion

    }

}
