using System;
using System.Collections.Generic;
using System.Linq;
using Dismoyo.Data;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class SalesOrderSwapSummaryDataProvider : DefaultTableDataProvider<SalesOrderSwapSummary>, ISalesOrderSwapSummaryDataProvider
    {

        #region Methods

        public IList<SalesOrderSwapSummary> GetDataByDocumentID(Guid documentID)
        {
            var salesOrderSwapSummaries = GetData().Where(p => p.DocumentID.Equals(documentID)).ToList();

            return salesOrderSwapSummaries;
        }


        public override void DeleteData(SalesOrderSwapSummary obj, bool useTransaction)
        {
            var salesOrderSwapDetailsDataProvider =
                DataConfiguration.GetDefaultDataProvider<ISalesOrderSwapDetailsDataProvider>();

            var deleteChilds = salesOrderSwapDetailsDataProvider.GetDataByDocumentIDAndProductID(
                obj.DocumentID, obj.ProductID);
            foreach (var child in deleteChilds)
                salesOrderSwapDetailsDataProvider.DeleteData(child, useTransaction);

            base.DeleteData(obj, useTransaction);
        }

        #endregion

    }

}
