using Dismoyo.Data;
using Dismoyo.Web.Security;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;

namespace Dismoyo.Ciptoning.Data
{

    public partial class SalesmanCallLogDataProvider : DefaultTableDataProvider<SalesmanCallLog>, ISalesmanCallLogDataProvider
    {

        #region Methods

        public IList<SalesmanCallLog> GetDataBySalesmanCallID(Guid salesmanCallID)
        {
            var salesmanCallLogs = GetData().Where(p => p.SalesmanCallID.Equals(salesmanCallID)).ToList();

            return salesmanCallLogs;
        }

        
        protected override void OnBeginInsertData(BeginOperationDataEventArgs<SalesmanCallLog> e)
        {
            e.Data.CreatedDate = DefaultDataContext.GetDBServerUtcDateTime();
            if (HttpContext.Current.User.Identity.IsAuthenticated)
                e.Data.CreatedByUserID = ((BasicGenericIdentity<IvUser>)HttpContext.Current.User.Identity).UserData.ID;

            base.OnBeginInsertData(e);
        }

        protected override void OnBeginDeleteData(BeginOperationDataEventArgs<SalesmanCallLog> e)
        {
            base.OnBeginDeleteData(e);
        }

        #endregion

    }

