namespace Dismoyo.Ciptoning.Data
{

    public partial class SalesmanProductDataProvider : DefaultTableDataProvider<SalesmanProduct>, ISalesmanProductDataProvider
    {

        #region Methods

        public IList<SalesmanProduct> GetDataBySalesmanID(Guid salesmanID)
        {
            var salesmanProducts = GetData().Where(p => p.SalesmanID.Equals(salesmanID)).ToList();

            return salesmanProducts;
        }

        #endregion

    }

