namespace Dismoyo.Ciptoning.Data
{

    public partial class SalesmanProductTargetDataProvider : DefaultTableDataProvider<SalesmanProductTarget>, ISalesmanProductTargetDataProvider
    {

        #region Methods

        public IList<SalesmanProductTarget> GetDataBySalesmanIDAndPeriodID(Guid salesmanID, DateTime periodID)
        {
            var salesmanProductTargets = GetData().Where(p => p.SalesmanID.Equals(salesmanID) &&
                (p.PeriodID == periodID)).ToList();

            return salesmanProductTargets;
        }
        
        #endregion

    }

