namespace Dismoyo.Ciptoning.Data
{

    public partial class SiteProductDataProvider : DefaultTableDataProvider<SiteProduct>, ISiteProductDataProvider
    {

        #region Methods

        public IList<SiteProduct> GetDataBySiteID(Guid siteID)
        {
            var siteProducts = GetData().Where(p => p.SiteID.Equals(siteID)).ToList();

            return siteProducts;
        }

        #endregion

    }

