using System;
using System.Collections.Generic;
using System.Linq;
using Dismoyo.Data;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class StockOnHandPeriodicDataProvider : DefaultTableDataProvider<StockOnHandPeriodic>, IStockOnHandPeriodicDataProvider
    {

        #region Methods

        public IList<StockOnHandPeriodic> GetDataByWarehouseID(Guid warehouseID)
        {
            var stockOpnameSummaries = GetData().Where(p => p.WarehouseID.Equals(warehouseID)).ToList();

            return stockOpnameSummaries;
        }

        public IList<StockOnHandPeriodic> GetDataByWarehouseIDAndPeriodDateID(Guid warehouseID, DateTime periodDateID)
        {
            var stockOpnameSummaries = GetData().Where(p => p.WarehouseID.Equals(warehouseID) &&
                (p.PeriodDateID == periodDateID)).ToList();

            return stockOpnameSummaries;
        }

        public IList<StockOnHandPeriodic> GetDataByWarehouseIDAndPeriodDateIDAndProductID(Guid warehouseID,
            DateTime periodDateID, int productID)
        {
            var stockOpnameSummaries = GetData().Where(p => p.WarehouseID.Equals(warehouseID) &&
                (p.PeriodDateID == periodDateID) && (p.ProductID == productID)).ToList();

            return stockOpnameSummaries;
        }

        #endregion

    }

}
