using System;
using System.Collections.Generic;
using System.Linq;
using Dismoyo.Data;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class StockOpnameSummaryDataProvider : DefaultTableDataProvider<StockOpnameSummary>, IStockOpnameSummaryDataProvider
    {

        #region Methods

        public IList<StockOpnameSummary> GetDataByDocumentID(Guid documentID)
        {
            var stockOpnameSummaries = GetData().Where(p => p.DocumentID.Equals(documentID)).ToList();

            return stockOpnameSummaries;
        }


        public override void DeleteData(StockOpnameSummary obj, bool useTransaction)
        {
            var stockOpnameDetailsDataProvider =
                DataConfiguration.GetDefaultDataProvider<IStockOpnameDetailsDataProvider>();

            var deleteChilds = stockOpnameDetailsDataProvider.GetDataByDocumentIDAndProductID(
                obj.DocumentID, obj.ProductID);
            foreach (var child in deleteChilds)
                stockOpnameDetailsDataProvider.DeleteData(child, useTransaction);

            base.DeleteData(obj, useTransaction);
        }

        #endregion

    }

}
