using System;
using System.Collections.Generic;
using System.Linq;
using Dismoyo.Data;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class StockReceiveSummaryDataProvider : DefaultTableDataProvider<StockReceiveSummary>, IStockReceiveSummaryDataProvider
    {

        #region Methods

        public IList<StockReceiveSummary> GetDataByDocumentID(Guid documentID)
        {
            var stockReceiveSummaries = GetData().Where(p => p.DocumentID.Equals(documentID)).ToList();

            return stockReceiveSummaries;
        }


        public override void DeleteData(StockReceiveSummary obj, bool useTransaction)
        {
            var stockReceiveDetailsDataProvider =
                DataConfiguration.GetDefaultDataProvider<IStockReceiveDetailsDataProvider>();

            var deleteChilds = stockReceiveDetailsDataProvider.GetDataByDocumentIDAndProductID(
                obj.DocumentID, obj.ProductID);
            foreach (var child in deleteChilds)
                stockReceiveDetailsDataProvider.DeleteData(child, useTransaction);

            base.DeleteData(obj, useTransaction);
        }

        #endregion

    }

}
