using System;
using System.Collections.Generic;
using System.Linq;
using Dismoyo.Data;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class StockTransactionDataProvider : DefaultTableDataProvider<StockTransaction>, IStockTransactionDataProvider
    {

        #region Methods

        public IList<StockTransaction> GetDataByDocumentID(Guid documentID)
        {
            var stockOpnameSummaries = GetData().Where(p => p.DocumentID.Equals(documentID)).ToList();

            return stockOpnameSummaries;
        }

        public IList<StockTransaction> GetDataByDocumentIDAndProductID(Guid documentID, int productID)
        {
            var stockOpnameSummaries = GetData().Where(p => p.DocumentID.Equals(documentID) &&
                (p.ProductID == productID)).ToList();

            return stockOpnameSummaries;
        }


        protected override void OnBeginInsertData(BeginOperationDataEventArgs<StockTransaction> e)
        {
            e.Data.CreatedDate = DefaultDataContext.GetDBServerUtcDateTime();

            base.OnBeginInsertData(e);
        }

        #endregion

    }

}
