using Dismoyo.Data;
using Dismoyo.Web.Security;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;

namespace Dismoyo.Ciptoning.Data
{

    public partial class UserRoleDataProvider : DefaultTableDataProvider<UserRole>, IUserRoleDataProvider
    {

        #region Methods

        public List<UserRole> GetDataByUserID(int userID)
        {
            var userRoles = GetData().Where(p => p.UserID == userID).ToList();

            return userRoles;
        }

        public List<UserRole> GetDataByRoleID(int roleID)
        {
            var userRoles = GetData().Where(p => p.RoleID == roleID).ToList();

            return userRoles;
        }


        protected override void OnBeginInsertData(BeginOperationDataEventArgs<UserRole> e)
        {
            e.Data.CreatedDate = DefaultDataContext.GetDBServerUtcDateTime();
            if (HttpContext.Current.User.Identity.IsAuthenticated)
                e.Data.CreatedByUserID = ((BasicGenericIdentity<IvUser>)HttpContext.Current.User.Identity).UserData.ID;

            base.OnBeginInsertData(e);
        }

        #endregion

    }

}
