using Dismoyo.Data;
using Dismoyo.Web.Security;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;

namespace Dismoyo.Ciptoning.Data
{

    public partial class UserRolePermissionDataProvider : DefaultTableDataProvider<UserRolePermission>, IUserRolePermissionDataProvider
    {

        #region Methods

        public List<UserRolePermission> GetDataByPermissionObjectID(string permissionObjectID)
        {
            var userRolePermissions = GetData().Where(p => p.PermissionObjectID == permissionObjectID).ToList();

            return userRolePermissions;
        }

        public List<UserRolePermission> GetDataByRoleID(int roleID)
        {
            var userRolePermissions = GetData().Where(p => !p.IsUser && (p.UserRoleID == roleID)).ToList();

            return userRolePermissions;
        }

        public List<UserRolePermission> GetDataByUserID(int userID)
        {
            var userRolePermissions = GetData().Where(p => p.IsUser && (p.UserRoleID == userID)).ToList();

            return userRolePermissions;
        }


        protected override void OnBeginInsertData(BeginOperationDataEventArgs<UserRolePermission> e)
        {
            e.Data.CreatedDate = DefaultDataContext.GetDBServerUtcDateTime();
            if (HttpContext.Current.User.Identity.IsAuthenticated)
                e.Data.CreatedByUserID = ((BasicGenericIdentity<IvUser>)HttpContext.Current.User.Identity).UserData.ID;

            base.OnBeginInsertData(e);
        }

        #endregion

    }

}
