using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vCustomerCategoryDataProvider : DefaultViewDataProvider<vCustomerCategory>, IvCustomerCategoryDataProvider
    {

        #region Methods

        protected override void OnInsertData(vCustomerCategory obj)
        {
            var customerCategoryDataProvider = DataConfiguration.GetDefaultDataProvider<ICustomerCategoryDataProvider>();

            var customerCategory = new CustomerCategory();

            customerCategory.Code = obj.Code;
            customerCategory.Name = obj.Name;
            customerCategory.ParentID = obj.ParentID;
            customerCategory.Group = obj.Group;

            customerCategoryDataProvider.InsertData(customerCategory);
        }

        protected override void OnUpdateData(vCustomerCategory obj)
        {
            var customerCategoryDataProvider = DataConfiguration.GetDefaultDataProvider<ICustomerCategoryDataProvider>();

            var customerCategory = customerCategoryDataProvider.GetData(obj.ID);

            customerCategory.Code = obj.Code;
            customerCategory.Name = obj.Name;
            customerCategory.ParentID = obj.ParentID;
            customerCategory.Group = obj.Group;

            customerCategoryDataProvider.UpdateData(customerCategory);
        }

        protected override void OnDeleteData(vCustomerCategory obj)
        {
            var customerCategoryDataProvider = DataConfiguration.GetDefaultDataProvider<ICustomerCategoryDataProvider>();

            var customerCategory = customerCategoryDataProvider.GetData(obj.ID);

            customerCategory.IsDeleted = true;

            customerCategoryDataProvider.UpdateData(customerCategory);
        }

        #endregion

    }

}
