using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vCustomerStockOnHandPeriodicDataProvider : DefaultViewDataProvider<vCustomerStockOnHandPeriodic>, IvCustomerStockOnHandPeriodicDataProvider
    {

        #region Methods
        protected override void OnInsertData(vCustomerStockOnHandPeriodic obj, bool useTransaction)
        {
            var customerStockOnHandPeriodicDataProvider = DataConfiguration.GetDefaultDataProvider<ICustomerStockOnHandPeriodicDataProvider>();

            var customerStockOnHandPeriodic = new CustomerStockOnHandPeriodic();

            customerStockOnHandPeriodic.ID = (obj.ID.Equals(Guid.Empty) ? Guid.NewGuid() : obj.ID);
            customerStockOnHandPeriodic.PeriodDate = obj.PeriodDate;
            customerStockOnHandPeriodic.SalesmanCallID = obj.SalesmanCallID;
            customerStockOnHandPeriodic.ProductID = obj.ProductID;
            customerStockOnHandPeriodic.Qty = obj.Qty;

            customerStockOnHandPeriodicDataProvider.InsertData(customerStockOnHandPeriodic, useTransaction);
        }

        protected override void OnUpdateData(vCustomerStockOnHandPeriodic obj, bool useTransaction)
        {
            var customerStockOnHandPeriodicDataProvider = DataConfiguration.GetDefaultDataProvider<ICustomerStockOnHandPeriodicDataProvider>();

            var customerStockOnHandPeriodic = customerStockOnHandPeriodicDataProvider.GetData(obj.ID);

            customerStockOnHandPeriodic.PeriodDate = obj.PeriodDate;
            customerStockOnHandPeriodic.SalesmanCallID = obj.SalesmanCallID;
            customerStockOnHandPeriodic.ProductID = obj.ProductID;
            customerStockOnHandPeriodic.Qty = obj.Qty;

            customerStockOnHandPeriodicDataProvider.UpdateData(customerStockOnHandPeriodic, useTransaction);
        }

        #endregion

    }

}
