using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vDiscountGroupProductDataProvider : DefaultViewDataProvider<vDiscountGroupProduct>, IvDiscountGroupProductDataProvider
    {
        
        #region Methods

        protected override void OnInsertData(vDiscountGroupProduct obj, bool useTransaction)
        {
            var discountGroupProductDataProvider = DataConfiguration.GetDefaultDataProvider<IDiscountGroupProductDataProvider>();

            var existingProduct = discountGroupProductDataProvider.GetData(obj.DiscountGroupID, obj.ProductID);
            if (existingProduct == null)
            {
                var discountGroupProduct = new DiscountGroupProduct();

                discountGroupProduct.DiscountGroupID = obj.DiscountGroupID;
                discountGroupProduct.ProductID = obj.ProductID;
                discountGroupProduct.DiscountStrata1ID = obj.DiscountStrata1ID;
                discountGroupProduct.DiscountStrata2ID = obj.DiscountStrata2ID;
                discountGroupProduct.DiscountStrata3ID = obj.DiscountStrata3ID;
                discountGroupProduct.DiscountStrata4ID = obj.DiscountStrata4ID;
                discountGroupProduct.DiscountStrata5ID = obj.DiscountStrata5ID;

                discountGroupProductDataProvider.InsertData(discountGroupProduct, useTransaction);
            }
        }

        protected override void OnUpdateData(vDiscountGroupProduct obj, bool useTransaction)
        {
            var discountGroupProductDataProvider = DataConfiguration.GetDefaultDataProvider<IDiscountGroupProductDataProvider>();
            
            var discountGroupProduct = discountGroupProductDataProvider.GetData(obj.DiscountGroupID, obj.ProductID);
                        
            discountGroupProduct.ProductID = obj.ProductID;
            discountGroupProduct.DiscountStrata1ID = obj.DiscountStrata1ID;
            discountGroupProduct.DiscountStrata2ID = obj.DiscountStrata2ID;
            discountGroupProduct.DiscountStrata3ID = obj.DiscountStrata3ID;
            discountGroupProduct.DiscountStrata4ID = obj.DiscountStrata4ID;
            discountGroupProduct.DiscountStrata5ID = obj.DiscountStrata5ID;

            discountGroupProductDataProvider.UpdateData(discountGroupProduct, useTransaction);
        }

        protected override void OnDeleteData(vDiscountGroupProduct obj, bool useTransaction)
        {
            var discountGroupProductDataProvider = DataConfiguration.GetDefaultDataProvider<IDiscountGroupProductDataProvider>();

            var discountGroupProduct = discountGroupProductDataProvider.GetData(obj.DiscountGroupID, obj.ProductID);

            discountGroupProductDataProvider.DeleteData(discountGroupProduct, useTransaction);
        }

        #endregion
        
    }

}
