using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vPromoMaterialActivityDataProvider : DefaultViewDataProvider<vPromoMaterialActivity>, IvPromoMaterialActivityDataProvider
    {

        #region Methods

        protected override void OnInsertData(vPromoMaterialActivity obj)
        {
            var promoMaterialActivityDataProvider = DataConfiguration.GetDefaultDataProvider<IPromoMaterialActivityDataProvider>();

            var promoMaterialActivity = new PromoMaterialActivity();

            promoMaterialActivity.ID = (obj.ID.Equals(Guid.Empty) ? Guid.NewGuid() : obj.ID);
            promoMaterialActivity.ActivityDate = obj.ActivityDate;
            promoMaterialActivity.SalesmanCallID = obj.SalesmanCallID;
            promoMaterialActivity.AttachmentFile = obj.AttachmentFile;

            promoMaterialActivityDataProvider.InsertData(promoMaterialActivity);
        }

        protected override void OnUpdateData(vPromoMaterialActivity obj)
        {
            var promoMaterialActivityDataProvider = DataConfiguration.GetDefaultDataProvider<IPromoMaterialActivityDataProvider>();

            var promoMaterialActivity = promoMaterialActivityDataProvider.GetData(obj.ID);

            promoMaterialActivity.ActivityDate = obj.ActivityDate;
            promoMaterialActivity.SalesmanCallID = obj.SalesmanCallID;
            promoMaterialActivity.AttachmentFile = obj.AttachmentFile;

            promoMaterialActivityDataProvider.UpdateData(promoMaterialActivity);
        }

        #endregion

    }

}
