using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vSalesOrderSampleDetailsDataProvider : DefaultViewDataProvider<vSalesOrderSampleDetails>, IvSalesOrderSampleDetailsDataProvider
    {

        #region Methods

        protected override void OnInsertData(vSalesOrderSampleDetails obj, bool useTransaction)
        {
            var salesOrderSampleDetailsDataProvider = DataConfiguration.GetDefaultDataProvider<ISalesOrderSampleDetailsDataProvider>();

            var salesOrderSampleDetails = new SalesOrderSampleDetails();

            salesOrderSampleDetails.DocumentID = obj.DocumentID;
            salesOrderSampleDetails.ProductID = obj.ProductID;
            salesOrderSampleDetails.ProductLotID = obj.ProductLotID;

            salesOrderSampleDetails.QtyOnHand = obj.QtyOnHand;

            salesOrderSampleDetails.QtyConvL = obj.QtyConvL;
            salesOrderSampleDetails.QtyConvM = obj.QtyConvM;
            salesOrderSampleDetails.QtyConvS = obj.QtyConvS;

            salesOrderSampleDetails.Qty = obj.Qty;

            salesOrderSampleDetailsDataProvider.InsertData(salesOrderSampleDetails, useTransaction);
        }

        protected override void OnUpdateData(vSalesOrderSampleDetails obj, bool useTransaction)
        {
            var salesOrderSampleDetailsDataProvider = DataConfiguration.GetDefaultDataProvider<ISalesOrderSampleDetailsDataProvider>();

            var salesOrderSampleDetails = salesOrderSampleDetailsDataProvider.GetData(obj.DocumentID, obj.ProductID,
                obj.ProductLotID);

            salesOrderSampleDetails.QtyOnHand = obj.QtyOnHand;

            salesOrderSampleDetails.QtyConvL = obj.QtyConvL;
            salesOrderSampleDetails.QtyConvM = obj.QtyConvM;
            salesOrderSampleDetails.QtyConvS = obj.QtyConvS;

            salesOrderSampleDetails.Qty = obj.Qty;

            salesOrderSampleDetailsDataProvider.UpdateData(salesOrderSampleDetails, useTransaction);
        }

        #endregion

    }

}
