    public partial class vSalesmanCallLogDataProvider : DefaultViewDataProvider<vSalesmanCallLog>, IvSalesmanCallLogDataProvider
    {

        #region Methods

        protected override void OnInsertData(vSalesmanCallLog obj, bool useTransaction)
        {
            var salesmanCallLogDataProvider = DataConfiguration.GetDefaultDataProvider<ISalesmanCallLogDataProvider>();

            var salesmanCallLog = new SalesmanCallLog();

            salesmanCallLog.ID = (obj.ID.Equals(Guid.Empty) ? Guid.NewGuid() : obj.ID);
            salesmanCallLog.SalesmanCallID = obj.SalesmanCallID;
            salesmanCallLog.CheckedDate = obj.CheckedDate;
            salesmanCallLog.CheckedLongitude = obj.CheckedLongitude;
            salesmanCallLog.CheckedLatitude = obj.CheckedLatitude;
            salesmanCallLog.IsCheckedIn = obj.IsCheckedIn;

            salesmanCallLogDataProvider.InsertData(salesmanCallLog, useTransaction);
        }

        protected override void OnUpdateData(vSalesmanCallLog obj, bool useTransaction)
        {
            var salesmanCallLogDataProvider = DataConfiguration.GetDefaultDataProvider<ISalesmanCallLogDataProvider>();

            var salesmanCallLog = salesmanCallLogDataProvider.GetData(obj.ID);

            salesmanCallLog.SalesmanCallID = obj.SalesmanCallID;
            salesmanCallLog.CheckedDate = obj.CheckedDate;
            salesmanCallLog.CheckedLongitude = obj.CheckedLongitude;
            salesmanCallLog.CheckedLatitude = obj.CheckedLatitude;
            salesmanCallLog.IsCheckedIn = obj.IsCheckedIn;

            salesmanCallLogDataProvider.UpdateData(salesmanCallLog, useTransaction);
        }

        #endregion

