using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vSiteProductDataProvider : DefaultViewDataProvider<vSiteProduct>, IvSiteProductDataProvider
    {
        
        #region Methods

        protected override void OnInsertData(vSiteProduct obj, bool useTransaction)
        {
            var siteProductDataProvider = DataConfiguration.GetDefaultDataProvider<ISiteProductDataProvider>();

            var existingProduct = siteProductDataProvider.GetData(obj.SiteID, obj.ProductID);
            if (existingProduct == null)
            {
                var siteProduct = new SiteProduct();

                siteProduct.SiteID = obj.SiteID;
                siteProduct.ProductID = obj.ProductID;

                siteProductDataProvider.InsertData(siteProduct, useTransaction);
            }
        }

        protected override void OnUpdateData(vSiteProduct obj, bool useTransaction)
        {
            var siteProductDataProvider = DataConfiguration.GetDefaultDataProvider<ISiteProductDataProvider>();
            
            var siteProduct = siteProductDataProvider.GetData(obj.SiteID, obj.ProductID);

            siteProduct.ProductID = obj.ProductID;

            siteProductDataProvider.UpdateData(siteProduct, useTransaction);
        }

        protected override void OnDeleteData(vSiteProduct obj, bool useTransaction)
        {
            var siteProductDataProvider = DataConfiguration.GetDefaultDataProvider<ISiteProductDataProvider>();

            var siteProduct = siteProductDataProvider.GetData(obj.SiteID, obj.ProductID);

            siteProductDataProvider.DeleteData(siteProduct, useTransaction);
        }

        #endregion
        
    }

}
