using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vStockChangesDetailsDataProvider : DefaultViewDataProvider<vStockChangesDetails>, IvStockChangesDetailsDataProvider
    {

        #region Methods

        protected override void OnInsertData(vStockChangesDetails obj, bool useTransaction)
        {
            var stockChangesDetailsDataProvider = DataConfiguration.GetDefaultDataProvider<IStockChangesDetailsDataProvider>();

            var stockChangesDetails = new StockChangesDetails();

            stockChangesDetails.DocumentID = obj.DocumentID;
            stockChangesDetails.ProductID = obj.ProductID;
            stockChangesDetails.ProductLotID = obj.ProductLotID;

            stockChangesDetails.QtyOnHand = obj.QtyOnHand;
            
            stockChangesDetails.QtyConvL = obj.QtyConvL;
            stockChangesDetails.QtyConvM = obj.QtyConvM;
            stockChangesDetails.QtyConvS = obj.QtyConvS;
            
            stockChangesDetails.Qty = obj.Qty;

            stockChangesDetailsDataProvider.InsertData(stockChangesDetails, useTransaction);
        }

        protected override void OnUpdateData(vStockChangesDetails obj, bool useTransaction)
        {
            var stockChangesDetailsDataProvider = DataConfiguration.GetDefaultDataProvider<IStockChangesDetailsDataProvider>();

            var stockChangesDetails = stockChangesDetailsDataProvider.GetData(obj.DocumentID, obj.ProductID,
                obj.ProductLotID);

            stockChangesDetails.QtyOnHand = obj.QtyOnHand;

            stockChangesDetails.QtyConvL = obj.QtyConvL;
            stockChangesDetails.QtyConvM = obj.QtyConvM;
            stockChangesDetails.QtyConvS = obj.QtyConvS;

            stockChangesDetails.Qty = obj.Qty;

            stockChangesDetailsDataProvider.UpdateData(stockChangesDetails, useTransaction);
        }

        #endregion

    }

}
