using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vStockChangesSummaryDataProvider : DefaultViewDataProvider<vStockChangesSummary>, IvStockChangesSummaryDataProvider
    {

        #region Methods

        protected override void OnInsertData(vStockChangesSummary obj, bool useTransaction)
        {
            var stockChangesSummaryDataProvider = DataConfiguration.GetDefaultDataProvider<IStockChangesSummaryDataProvider>();

            var stockChangesSummary = new StockChangesSummary();

            stockChangesSummary.DocumentID = obj.DocumentID;
            stockChangesSummary.ProductID = obj.ProductID;

            stockChangesSummary.QtyOnHand = obj.QtyOnHand;

            stockChangesSummary.QtyConvL = obj.QtyConvL;
            stockChangesSummary.QtyConvM = obj.QtyConvM;
            stockChangesSummary.QtyConvS = obj.QtyConvS;

            stockChangesSummary.Qty = obj.Qty;

            stockChangesSummaryDataProvider.InsertData(stockChangesSummary, useTransaction);
            if ((obj.ChildDetails != null) && (obj.ChildDetails.Count > 0))
            {
                // Insert new child data.
                var vStockChangesDetailsDataProvider =
                    DataConfiguration.GetDefaultDataProvider<IvStockChangesDetailsDataProvider>();
                foreach (var details in obj.ChildDetails)
                {
                    details.DocumentID = stockChangesSummary.DocumentID;
                    details.ProductID = stockChangesSummary.ProductID;
                    vStockChangesDetailsDataProvider.InsertData(details, useTransaction);
                }
            }
        }

        protected override void OnUpdateData(vStockChangesSummary obj, bool useTransaction)
        {
            var stockChangesSummaryDataProvider = DataConfiguration.GetDefaultDataProvider<IStockChangesSummaryDataProvider>();

            var stockChangesSummary = stockChangesSummaryDataProvider.GetData(obj.DocumentID, obj.ProductID);

            stockChangesSummary.QtyOnHand = obj.QtyOnHand;

            stockChangesSummary.QtyConvL = obj.QtyConvL;
            stockChangesSummary.QtyConvM = obj.QtyConvM;
            stockChangesSummary.QtyConvS = obj.QtyConvS;

            stockChangesSummary.Qty = obj.Qty;

            stockChangesSummaryDataProvider.UpdateData(stockChangesSummary, useTransaction);
            if ((obj.ChildDetails != null) && (obj.ChildDetails.Count > 0))
            {
                var stockChangesDetailsDataProvider =
                    DataConfiguration.GetDefaultDataProvider<IStockChangesDetailsDataProvider>();
                var vStockChangesDetailsDataProvider =
                    DataConfiguration.GetDefaultDataProvider<IvStockChangesDetailsDataProvider>();

                var insertChilds = obj.ChildDetails.ToList();
                var deleteChilds = stockChangesDetailsDataProvider.GetDataByDocumentIDAndProductID(
                    stockChangesSummary.DocumentID, stockChangesSummary.ProductID);
                var updateChilds = new List<vStockChangesDetails>();
                int i = 0;
                while (i < deleteChilds.Count)
                {
                    var data = insertChilds.SingleOrDefault(p => (p.ProductLotID == deleteChilds[i].ProductLotID));
                    if (data != null)
                    {
                        insertChilds.Remove(data);
                        deleteChilds.RemoveAt(i);
                        updateChilds.Add(data);
                        continue;
                    }

                    i++;
                }

                // Removes existing and unused child data.
                foreach (var child in deleteChilds)
                    stockChangesDetailsDataProvider.DeleteData(child, useTransaction);

                // Update existing child data.
                foreach (var child in updateChilds)
                    vStockChangesDetailsDataProvider.UpdateData(child, useTransaction);

                // Insert new child data.
                foreach (var child in insertChilds)
                {
                    child.DocumentID = stockChangesSummary.DocumentID;
                    child.ProductID = stockChangesSummary.ProductID;
                    vStockChangesDetailsDataProvider.InsertData(child, useTransaction);
                }
            }
        }

        #endregion

    }

}
