using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vStockDisposalSummaryDataProvider : DefaultViewDataProvider<vStockDisposalSummary>, IvStockDisposalSummaryDataProvider
    {

        #region Methods

        protected override void OnInsertData(vStockDisposalSummary obj, bool useTransaction)
        {
            var stockDisposalSummaryDataProvider = DataConfiguration.GetDefaultDataProvider<IStockDisposalSummaryDataProvider>();

            var stockDisposalSummary = new StockDisposalSummary();

            stockDisposalSummary.DocumentID = obj.DocumentID;
            stockDisposalSummary.ProductID = obj.ProductID;

            stockDisposalSummary.QtyOnHand = obj.QtyOnHand;

            stockDisposalSummary.QtyConvL = obj.QtyConvL;
            stockDisposalSummary.QtyConvM = obj.QtyConvM;
            stockDisposalSummary.QtyConvS = obj.QtyConvS;

            stockDisposalSummary.Qty = obj.Qty;

            stockDisposalSummaryDataProvider.InsertData(stockDisposalSummary, useTransaction);
            if ((obj.ChildDetails != null) && (obj.ChildDetails.Count > 0))
            {
                // Insert new child data.
                var vStockDisposalDetailsDataProvider =
                    DataConfiguration.GetDefaultDataProvider<IvStockDisposalDetailsDataProvider>();
                foreach (var details in obj.ChildDetails)
                {
                    details.DocumentID = stockDisposalSummary.DocumentID;
                    details.ProductID = stockDisposalSummary.ProductID;
                    vStockDisposalDetailsDataProvider.InsertData(details, useTransaction);
                }
            }
        }

        protected override void OnUpdateData(vStockDisposalSummary obj, bool useTransaction)
        {
            var stockDisposalSummaryDataProvider = DataConfiguration.GetDefaultDataProvider<IStockDisposalSummaryDataProvider>();

            var stockDisposalSummary = stockDisposalSummaryDataProvider.GetData(obj.DocumentID, obj.ProductID);

            stockDisposalSummary.QtyOnHand = obj.QtyOnHand;

            stockDisposalSummary.QtyConvL = obj.QtyConvL;
            stockDisposalSummary.QtyConvM = obj.QtyConvM;
            stockDisposalSummary.QtyConvS = obj.QtyConvS;

            stockDisposalSummary.Qty = obj.Qty;

            stockDisposalSummaryDataProvider.UpdateData(stockDisposalSummary, useTransaction);
            if ((obj.ChildDetails != null) && (obj.ChildDetails.Count > 0))
            {
                var stockDisposalDetailsDataProvider =
                    DataConfiguration.GetDefaultDataProvider<IStockDisposalDetailsDataProvider>();
                var vStockDisposalDetailsDataProvider =
                    DataConfiguration.GetDefaultDataProvider<IvStockDisposalDetailsDataProvider>();

                var insertChilds = obj.ChildDetails.ToList();
                var deleteChilds = stockDisposalDetailsDataProvider.GetDataByDocumentIDAndProductID(
                    stockDisposalSummary.DocumentID, stockDisposalSummary.ProductID);
                var updateChilds = new List<vStockDisposalDetails>();
                int i = 0;
                while (i < deleteChilds.Count)
                {
                    var data = insertChilds.SingleOrDefault(p => (p.ProductLotID == deleteChilds[i].ProductLotID));
                    if (data != null)
                    {
                        insertChilds.Remove(data);
                        deleteChilds.RemoveAt(i);
                        updateChilds.Add(data);
                        continue;
                    }

                    i++;
                }

                // Removes existing and unused child data.
                foreach (var child in deleteChilds)
                    stockDisposalDetailsDataProvider.DeleteData(child, useTransaction);

                // Update existing child data.
                foreach (var child in updateChilds)
                    vStockDisposalDetailsDataProvider.UpdateData(child, useTransaction);

                // Insert new child data.
                foreach (var child in insertChilds)
                {
                    child.DocumentID = stockDisposalSummary.DocumentID;
                    child.ProductID = stockDisposalSummary.ProductID;
                    vStockDisposalDetailsDataProvider.InsertData(child, useTransaction);
                }
            }
        }

        #endregion

    }

}
