    public partial class vStockOpnameDetailsDataProvider : DefaultViewDataProvider<vStockOpnameDetails>, IvStockOpnameDetailsDataProvider
    {

        #region Methods

        protected override void OnInsertData(vStockOpnameDetails obj, bool useTransaction)
        {
            var stockOpnameDetailsDataProvider = DataConfiguration.GetDefaultDataProvider<IStockOpnameDetailsDataProvider>();

            var stockOpnameDetails = new StockOpnameDetails();

            stockOpnameDetails.DocumentID = obj.DocumentID;
            stockOpnameDetails.ProductID = obj.ProductID;
            stockOpnameDetails.ProductLotID = obj.ProductLotID;

            stockOpnameDetails.QtyOnHandGood = obj.QtyOnHandGood;
            stockOpnameDetails.QtyOnHandHold = obj.QtyOnHandHold;
            stockOpnameDetails.QtyOnHandBad = obj.QtyOnHandBad;

            stockOpnameDetails.QtyConvLGood = obj.QtyConvLGood;
            stockOpnameDetails.QtyConvMGood = obj.QtyConvMGood;
            stockOpnameDetails.QtyConvSGood = obj.QtyConvSGood;
            stockOpnameDetails.QtyConvLHold = obj.QtyConvLHold;
            stockOpnameDetails.QtyConvMHold = obj.QtyConvMHold;
            stockOpnameDetails.QtyConvSHold = obj.QtyConvSHold;
            stockOpnameDetails.QtyConvLBad = obj.QtyConvLBad;
            stockOpnameDetails.QtyConvMBad = obj.QtyConvMBad;
            stockOpnameDetails.QtyConvSBad = obj.QtyConvSBad;

            stockOpnameDetails.QtyGood = obj.QtyGood;
            stockOpnameDetails.QtyHold = obj.QtyHold;
            stockOpnameDetails.QtyBad = obj.QtyBad;

            stockOpnameDetailsDataProvider.InsertData(stockOpnameDetails, useTransaction);
        }

        protected override void OnUpdateData(vStockOpnameDetails obj, bool useTransaction)
        {
            var stockOpnameDetailsDataProvider = DataConfiguration.GetDefaultDataProvider<IStockOpnameDetailsDataProvider>();

            var stockOpnameDetails = stockOpnameDetailsDataProvider.GetData(obj.DocumentID, obj.ProductID,
                obj.ProductLotID);

            stockOpnameDetails.QtyOnHandGood = obj.QtyOnHandGood;
            stockOpnameDetails.QtyOnHandHold = obj.QtyOnHandHold;
            stockOpnameDetails.QtyOnHandBad = obj.QtyOnHandBad;
            
            stockOpnameDetails.QtyConvLGood = obj.QtyConvLGood;
            stockOpnameDetails.QtyConvMGood = obj.QtyConvMGood;
            stockOpnameDetails.QtyConvSGood = obj.QtyConvSGood;
            stockOpnameDetails.QtyConvLHold = obj.QtyConvLHold;
            stockOpnameDetails.QtyConvMHold = obj.QtyConvMHold;
            stockOpnameDetails.QtyConvSHold = obj.QtyConvSHold;
            stockOpnameDetails.QtyConvLBad = obj.QtyConvLBad;
            stockOpnameDetails.QtyConvMBad = obj.QtyConvMBad;
            stockOpnameDetails.QtyConvSBad = obj.QtyConvSBad;

            stockOpnameDetails.QtyGood = obj.QtyGood;
            stockOpnameDetails.QtyHold = obj.QtyHold;
            stockOpnameDetails.QtyBad = obj.QtyBad;

            stockOpnameDetailsDataProvider.UpdateData(stockOpnameDetails, useTransaction);
