using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vStockReceiveDetailsDataProvider : DefaultViewDataProvider<vStockReceiveDetails>, IvStockReceiveDetailsDataProvider
    {

        #region Methods

        protected override void OnInsertData(vStockReceiveDetails obj, bool useTransaction)
        {
            var stockReceiveDetailsDataProvider = DataConfiguration.GetDefaultDataProvider<IStockReceiveDetailsDataProvider>();

            var stockReceiveDetails = new StockReceiveDetails();

            stockReceiveDetails.DocumentID = obj.DocumentID;
            stockReceiveDetails.ProductID = obj.ProductID;
            stockReceiveDetails.ProductLotID = obj.ProductLotID;

            stockReceiveDetails.QtyOnHand = obj.QtyOnHand;

            stockReceiveDetails.QtyConvL = obj.QtyConvL;
            stockReceiveDetails.QtyConvM = obj.QtyConvM;
            stockReceiveDetails.QtyConvS = obj.QtyConvS;

            stockReceiveDetails.Qty = obj.Qty;

            stockReceiveDetailsDataProvider.InsertData(stockReceiveDetails, useTransaction);
        }

        protected override void OnUpdateData(vStockReceiveDetails obj, bool useTransaction)
        {
            var stockReceiveDetailsDataProvider = DataConfiguration.GetDefaultDataProvider<IStockReceiveDetailsDataProvider>();

            var stockReceiveDetails = stockReceiveDetailsDataProvider.GetData(obj.DocumentID, obj.ProductID,
                obj.ProductLotID);

            stockReceiveDetails.QtyOnHand = obj.QtyOnHand;

            stockReceiveDetails.QtyConvL = obj.QtyConvL;
            stockReceiveDetails.QtyConvM = obj.QtyConvM;
            stockReceiveDetails.QtyConvS = obj.QtyConvS;

            stockReceiveDetails.Qty = obj.Qty;

            stockReceiveDetailsDataProvider.UpdateData(stockReceiveDetails, useTransaction);
        }

        #endregion

    }

}
