using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vStockTransferSummaryDataProvider : DefaultViewDataProvider<vStockTransferSummary>, IvStockTransferSummaryDataProvider
    {

        #region Methods

        protected override void OnInsertData(vStockTransferSummary obj, bool useTransaction)
        {
            var stockTransferSummaryDataProvider = DataConfiguration.GetDefaultDataProvider<IStockTransferSummaryDataProvider>();

            var stockTransferSummary = new StockTransferSummary();

            stockTransferSummary.DocumentID = obj.DocumentID;
            stockTransferSummary.ProductID = obj.ProductID;

            stockTransferSummary.QtyOnHandGood = obj.QtyOnHandGood;
            stockTransferSummary.QtyOnHandHold = obj.QtyOnHandHold;
            stockTransferSummary.QtyOnHandBad = obj.QtyOnHandBad;

            stockTransferSummary.QtyConvLGood = obj.QtyConvLGood;
            stockTransferSummary.QtyConvMGood = obj.QtyConvMGood;
            stockTransferSummary.QtyConvSGood = obj.QtyConvSGood;
            stockTransferSummary.QtyConvLHold = obj.QtyConvLHold;
            stockTransferSummary.QtyConvMHold = obj.QtyConvMHold;
            stockTransferSummary.QtyConvSHold = obj.QtyConvSHold;
            stockTransferSummary.QtyConvLBad = obj.QtyConvLBad;
            stockTransferSummary.QtyConvMBad = obj.QtyConvMBad;
            stockTransferSummary.QtyConvSBad = obj.QtyConvSBad;

            stockTransferSummary.QtyGood = obj.QtyGood;
            stockTransferSummary.QtyHold = obj.QtyHold;
            stockTransferSummary.QtyBad = obj.QtyBad;

            stockTransferSummaryDataProvider.InsertData(stockTransferSummary, useTransaction);
            if ((obj.ChildDetails != null) && (obj.ChildDetails.Count > 0))
            {
                // Insert new child data.
                var vStockTransferDetailsDataProvider =
                    DataConfiguration.GetDefaultDataProvider<IvStockTransferDetailsDataProvider>();
                foreach (var details in obj.ChildDetails)
                {
                    details.DocumentID = stockTransferSummary.DocumentID;
                    details.ProductID = stockTransferSummary.ProductID;
                    vStockTransferDetailsDataProvider.InsertData(details, useTransaction);
                }
            }
        }

        protected override void OnUpdateData(vStockTransferSummary obj, bool useTransaction)
        {
            var stockTransferSummaryDataProvider = DataConfiguration.GetDefaultDataProvider<IStockTransferSummaryDataProvider>();

            var stockTransferSummary = stockTransferSummaryDataProvider.GetData(obj.DocumentID, obj.ProductID);

            stockTransferSummary.QtyOnHandGood = obj.QtyOnHandGood;
            stockTransferSummary.QtyOnHandHold = obj.QtyOnHandHold;
            stockTransferSummary.QtyOnHandBad = obj.QtyOnHandBad;

            stockTransferSummary.QtyConvLGood = obj.QtyConvLGood;
            stockTransferSummary.QtyConvMGood = obj.QtyConvMGood;
            stockTransferSummary.QtyConvSGood = obj.QtyConvSGood;
            stockTransferSummary.QtyConvLHold = obj.QtyConvLHold;
            stockTransferSummary.QtyConvMHold = obj.QtyConvMHold;
            stockTransferSummary.QtyConvSHold = obj.QtyConvSHold;
            stockTransferSummary.QtyConvLBad = obj.QtyConvLBad;
            stockTransferSummary.QtyConvMBad = obj.QtyConvMBad;
            stockTransferSummary.QtyConvSBad = obj.QtyConvSBad;

            stockTransferSummary.QtyGood = obj.QtyGood;
            stockTransferSummary.QtyHold = obj.QtyHold;
            stockTransferSummary.QtyBad = obj.QtyBad;

            stockTransferSummaryDataProvider.UpdateData(stockTransferSummary, useTransaction);
            if ((obj.ChildDetails != null) && (obj.ChildDetails.Count > 0))
            {
                var stockTransferDetailsDataProvider =
                    DataConfiguration.GetDefaultDataProvider<IStockTransferDetailsDataProvider>();
                var vStockTransferDetailsDataProvider =
                    DataConfiguration.GetDefaultDataProvider<IvStockTransferDetailsDataProvider>();

                var insertChilds = obj.ChildDetails.ToList();
                var deleteChilds = stockTransferDetailsDataProvider.GetDataByDocumentIDAndProductID(
                    stockTransferSummary.DocumentID, stockTransferSummary.ProductID);
                var updateChilds = new List<vStockTransferDetails>();
                int i = 0;
                while (i < deleteChilds.Count)
                {
                    var data = insertChilds.SingleOrDefault(p => (p.ProductLotID == deleteChilds[i].ProductLotID));
                    if (data != null)
                    {
                        insertChilds.Remove(data);
                        deleteChilds.RemoveAt(i);
                        updateChilds.Add(data);
                        continue;
                    }

                    i++;
                }

                // Removes existing and unused child data.
                foreach (var child in deleteChilds)
                    stockTransferDetailsDataProvider.DeleteData(child, useTransaction);

                // Update existing child data.
                foreach (var child in updateChilds)
                    vStockTransferDetailsDataProvider.UpdateData(child, useTransaction);

                // Insert new child data.
                foreach (var child in insertChilds)
                {
                    child.DocumentID = stockTransferSummary.DocumentID;
                    child.ProductID = stockTransferSummary.ProductID;
                    vStockTransferDetailsDataProvider.InsertData(child, useTransaction);
                }
            }
        }

        #endregion

    }

}
