using System;
using System.Collections.Generic;
using System.Linq;
//using WebMatrix.WebData;

namespace Dismoyo.Ciptoning.Data
{

    public partial class vSyncHistoryDataProvider : DefaultViewDataProvider<vSyncHistory>, IvSyncHistoryDataProvider
    {

        #region Methods

        protected override void OnInsertData(vSyncHistory obj)
        {
            var syncHistoryDataProvider = DataConfiguration.GetDefaultDataProvider<ISyncHistoryDataProvider>();

            var syncHistory = new SyncHistory();

            syncHistory.SiteCode = obj.SiteCode;
            syncHistory.LastSyncDataDate = obj.LastSyncDataDate;
            syncHistory.LastSyncDate = obj.LastSyncDate;

            syncHistoryDataProvider.InsertData(syncHistory);
        }

        protected override void OnUpdateData(vSyncHistory obj)
        {
            var syncHistoryDataProvider = DataConfiguration.GetDefaultDataProvider<ISyncHistoryDataProvider>();

            var syncHistory = syncHistoryDataProvider.GetData(obj.SiteID);

            syncHistory.LastSyncDataDate = obj.LastSyncDataDate;
            syncHistory.LastSyncDate = obj.LastSyncDate;

            syncHistoryDataProvider.UpdateData(syncHistory);
        }

        protected override void OnDeleteData(vSyncHistory obj)
        {
            var syncHistoryDataProvider = DataConfiguration.GetDefaultDataProvider<ISyncHistoryDataProvider>();

            var syncHistory = syncHistoryDataProvider.GetData(obj.SiteID);

            // Do Nothing

            syncHistoryDataProvider.UpdateData(syncHistory);
        }

        #endregion

    }

}
