    public partial class vSystemLookupDataProvider : DefaultViewDataProvider<vSystemLookup>, IvSystemLookupDataProvider
    {
        
        #region Methods

        protected override void OnInsertData(vSystemLookup obj)
        {
            var systemLookupDataProvider = DataConfiguration.GetDefaultDataProvider<ISystemLookupDataProvider>();
            
            var systemLookup = new SystemLookup();

            systemLookup.Name = obj.Name;
            systemLookup.Value_Boolean = obj.Value_Boolean;
            systemLookup.Value_Int32 = obj.Value_Int32;
            systemLookup.Value_Double = obj.Value_Double;
            systemLookup.Value_String = obj.Value_String;
            systemLookup.Value_Guid = obj.Value_Guid;
            systemLookup.Value_DateTime = obj.Value_DateTime;
            systemLookup.ParentID = obj.ParentID;
            systemLookup.Group = obj.Group;
            systemLookup.SortIndex = obj.SortIndex;
            systemLookup.IsActive = obj.IsActive;
            systemLookup.IsAuthorization = obj.IsAuthorization;

            systemLookupDataProvider.InsertData(systemLookup);
        }

        protected override void OnUpdateData(vSystemLookup obj)
        {
            var systemLookupDataProvider = DataConfiguration.GetDefaultDataProvider<ISystemLookupDataProvider>();

            var systemLookup = systemLookupDataProvider.GetData(obj.ID);

            systemLookup.Name = obj.Name;
            systemLookup.Value_Boolean = obj.Value_Boolean;
            systemLookup.Value_Int32 = obj.Value_Int32;
            systemLookup.Value_Double = obj.Value_Double;
            systemLookup.Value_String = obj.Value_String;
            systemLookup.Value_Guid = obj.Value_Guid;
            systemLookup.Value_DateTime = obj.Value_DateTime;
            systemLookup.ParentID = obj.ParentID;
            systemLookup.Group = obj.Group;
            systemLookup.SortIndex = obj.SortIndex;
            systemLookup.IsActive = obj.IsActive;
            systemLookup.IsAuthorization = obj.IsAuthorization;

            systemLookupDataProvider.UpdateData(systemLookup);
        }

        protected override void OnDeleteData(vSystemLookup obj)
        {
